import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { DataService } from '../../services/data.service';
import { Channel, Group } from '../../models';

@Component({
  selector: 'app-channel-view',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="row">
      <div class="col-md-3">
        <div class="card">
          <div class="card-header">
            <h5 class="card-title mb-0">
              <i class="bi bi-hash me-2"></i>Channels
            </h5>
          </div>
          <div class="card-body p-0">
            <div *ngIf="groupChannels.length === 0" class="text-center py-3">
              <i class="bi bi-hash text-muted"></i>
              <p class="text-muted mt-2 mb-0">No channels yet</p>
            </div>

            <div *ngIf="groupChannels.length > 0" class="list-group list-group-flush">
              <a *ngFor="let channel of groupChannels" 
                 [routerLink]="['/group', groupId, 'channel', channel.id]"
                 class="list-group-item list-group-item-action"
                 [class.active]="channel.id === channelId">
                <i class="bi bi-hash me-2"></i>{{ channel.name }}
                <span *ngIf="isUserBanned(channel.id)" class="badge bg-danger rounded-pill ms-auto">Banned</span>
              </a>
            </div>
          </div>
        </div>
      </div>

      <div class="col-md-9">
        <div class="card">
          <div class="card-header d-flex justify-content-between align-items-center">
            <h5 class="card-title mb-0">
              <i class="bi bi-hash me-2"></i>{{ currentChannel?.name || 'Channel' }}
            </h5>
            <div class="btn-group" role="group">
              <button type="button" class="btn btn-outline-secondary btn-sm">
                <i class="bi bi-people"></i>
              </button>
              <button type="button" class="btn btn-outline-secondary btn-sm">
                <i class="bi bi-pin"></i>
              </button>
              <button type="button" class="btn btn-outline-secondary btn-sm">
                <i class="bi bi-gear"></i>
              </button>
            </div>
          </div>

          <div class="card-body" style="height: 500px; overflow-y: auto;">
            <!-- Banned user message -->
            <div *ngIf="isUserBannedFromCurrentChannel" class="text-center py-5">
              <i class="bi bi-shield-exclamation text-danger" style="font-size: 4rem;"></i>
              <h3 class="mt-3 text-danger">Access Denied</h3>
              <p class="text-muted">You have been banned from this channel.</p>
              <p class="text-muted">Contact a group administrator if you believe this is an error.</p>
            </div>

            <!-- Placeholder for Phase 1 -->
            <div *ngIf="!isUserBannedFromCurrentChannel" class="text-center py-5">
              <i class="bi bi-chat-dots text-primary" style="font-size: 4rem;"></i>
              <h3 class="mt-3">Chat Coming in Phase 2</h3>
              <p class="text-muted">This is a placeholder view for the channel.</p>
              <p class="text-muted">Real-time messaging will be implemented in Phase 2.</p>
              <div class="mt-4">
                <div class="alert alert-info" role="alert">
                  <i class="bi bi-info-circle me-2"></i>
                  <strong>Phase 1 Features:</strong> Authentication, Groups, Channels, User Management
                </div>
              </div>
            </div>
          </div>

          <div class="card-footer">
            <div class="input-group">
              <input type="text" class="form-control" placeholder="Type a message..." disabled>
              <button class="btn btn-primary" type="button" disabled>
                <i class="bi bi-send"></i>
              </button>
            </div>
            <small class="text-muted">Messaging will be available in Phase 2</small>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: []
})
export class ChannelViewComponent implements OnInit {
  groupId: string = '';
  channelId: string = '';
  currentChannel: Channel | null = null;
  groupChannels: Channel[] = [];
  isUserBannedFromCurrentChannel: boolean = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private authService: AuthService,
    private dataService: DataService
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.groupId = params['gid'];
      this.channelId = params['cid'];
      this.loadChannelData();
    });
  }

  private loadChannelData(): void {
    if (this.groupId && this.channelId) {
      this.currentChannel = this.dataService.getChannelById(this.channelId);
      this.groupChannels = this.dataService.getChannelsForGroup(this.groupId);

      const currentUser = this.authService.getCurrentUser();
      if (currentUser && this.currentChannel) {
        this.isUserBannedFromCurrentChannel = this.dataService.isUserBannedFromChannel(
          this.channelId, 
          currentUser.id
        );
      }
    }
  }

  isUserBanned(channelId: string): boolean {
    const currentUser = this.authService.getCurrentUser();
    return currentUser ? this.dataService.isUserBannedFromChannel(channelId, currentUser.id) : false;
  }
}
