import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule, Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-register',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  template: `
    <div class="row justify-content-center">
      <div class="col-md-6 col-lg-4">
        <div class="card shadow">
          <div class="card-body p-5">
            <div class="text-center mb-4">
              <i class="bi bi-person-plus text-primary" style="font-size: 3rem;"></i>
              <h2 class="mt-3 mb-1">Create Account</h2>
              <p class="text-muted">Join our chat community</p>
            </div>

            <div *ngIf="errorMessage" class="alert alert-danger" role="alert">
              {{ errorMessage }}
            </div>

            <div *ngIf="successMessage" class="alert alert-success" role="alert">
              {{ successMessage }}
            </div>

            <form (ngSubmit)="onSubmit()">
              <div class="mb-3">
                <label for="username" class="form-label">Username</label>
                <div class="input-group">
                  <span class="input-group-text">
                    <i class="bi bi-at"></i>
                  </span>
                  <input type="text" class="form-control" id="username" 
                         [(ngModel)]="username" name="username" 
                         placeholder="Choose a username" required>
                </div>
              </div>

              <div class="mb-3">
                <label for="password" class="form-label">Password</label>
                <div class="input-group">
                  <span class="input-group-text">
                    <i class="bi bi-lock"></i>
                  </span>
                  <input type="password" class="form-control" id="password" 
                         [(ngModel)]="password" name="password" 
                         placeholder="Create a password" required>
                </div>
                <div class="form-text">
                  Password is required
                </div>
              </div>

              <div class="mb-3">
                <label for="email" class="form-label">Email (Optional)</label>
                <div class="input-group">
                  <span class="input-group-text">
                    <i class="bi bi-envelope"></i>
                  </span>
                  <input type="email" class="form-control" id="email" 
                         [(ngModel)]="email" name="email" 
                         placeholder="Enter your email">
                </div>
              </div>

              <div class="d-grid">
                <button type="submit" class="btn btn-primary btn-lg" [disabled]="isLoading">
                  <span *ngIf="isLoading" class="spinner-border spinner-border-sm me-2" role="status"></span>
                  <i *ngIf="!isLoading" class="bi bi-person-plus me-2"></i>
                  {{ isLoading ? 'Creating Account...' : 'Create Account' }}
                </button>
              </div>
            </form>

            <div class="text-center mt-4">
              <p class="mb-0">Already have an account? 
                <a routerLink="/login" class="text-decoration-none">Sign in here</a>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: []
})
export class RegisterComponent {
  username: string = '';
  password: string = '';
  email: string = '';
  errorMessage: string = '';
  successMessage: string = '';
  isLoading: boolean = false;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {
    // Redirect if already logged in
    if (this.authService.isLoggedIn()) {
      this.router.navigate(['/dashboard']);
    }
  }

  onSubmit(): void {
    this.errorMessage = '';
    this.successMessage = '';
    this.isLoading = true;

    if (!this.username.trim()) {
      this.errorMessage = 'Username is required';
      this.isLoading = false;
      return;
    }

    if (!this.password.trim()) {
      this.errorMessage = 'Password is required';
      this.isLoading = false;
      return;
    }

    const result = this.authService.register(this.username, this.password, this.email || undefined);

    if (result.success) {
      this.successMessage = 'Account created successfully! Redirecting to login...';
      setTimeout(() => {
        this.router.navigate(['/login']);
      }, 2000);
    } else {
      this.errorMessage = result.error || 'Registration failed';
    }

    this.isLoading = false;
  }
}
